second_instance = Foo()

# –ü—Ä–æ–≤–µ—Ä—è–µ–º, —á—Ç–æ –∑–Ω–∞—á–µ–Ω–∏—è –ø–æ–ª—è –≤ –∫–∞–∂–¥–æ–º —ç–∫–∑–µ–º–ø–ª—è—Ä–µ –Ω–µ —Å—Å—ã–ª–∞—é—Ç—Å—è 
# –Ω–∞ –æ–¥–∏–Ω –∏ —Ç–æ—Ç –∂–µ —Å–ø–∏—Å–æ–∫
first_instance.field.append(1)
second_instance.field.append(2)

# first_instance.field
# [1]
# second_instance.field
# [2]
–ï—â–µ —Ä–∞–∑ —Ö–æ—á—É –∞–∫—Ü–µ–Ω—Ç–∏—Ä–æ–≤–∞—Ç—å –≤–Ω–∏–º–∞–Ω–∏–µ –Ω–∞ —Ç–æ–º, —á—Ç–æ –ø–æ–¥–æ–±–Ω–æ–≥–æ —Ä–æ–¥–∞ –ø–æ–¥—Ö–æ–¥—ã —è–≤–ª—è—é—Ç—Å—è –Ω–µ—è–≤–Ω—ã–º–∏, —É—Å–ª–æ–∂–Ω—è—é—Ç –ø–æ–¥–¥–µ—Ä–∂–∫—É –∏ —É–≤–µ–ª–∏—á–∏–≤–∞—é—Ç —Å–ª–æ–∂–Ω–æ—Å—Ç—å –∫–æ–¥–∞.
–°–∞–º —è –¥–æ–ø—É—Å–∫–∞—é –∏—Ö –ø—Ä–∏–º–µ–Ω–µ–Ω–∏–µ —Ç–æ–ª—å–∫–æ –¥–ª—è –∏–∑–æ–ª–∏—Ä–æ–≤–∞–Ω–Ω–æ–≥–æ –≤ —Å–µ–±–µ —Ñ—É–Ω–∫—Ü–∏–æ–Ω–∞–ª–∞, –ø–æ–ª–Ω–æ—Å—Ç—å—é –ø–æ–∫—Ä—ã—Ç–æ–≥–æ —Ç–µ—Å—Ç–∞–º–∏, —Å –∫—Ä–∞–π–Ω–µ –º–∞–ª–æ–π –≤–µ—Ä–æ—è—Ç–Ω–æ—Å—Ç—å—é —Ç—Ä–µ–±—É—é—â–µ–≥–æ –º–æ–¥–∏—Ñ–∏–∫–∞—Ü–∏—é –≤ –¥–∞–ª—å–Ω–µ–π—à–µ–º, –∞ –≥–ª–∞–≤–Ω–æ–µ, –ø–æ–∑–≤–æ–ª—è—é—â–µ–≥–æ –∑–Ω–∞—á–∏—Ç–µ–ª—å–Ω–æ —Å–æ–∫—Ä–∞—Ç–∏—Ç—å –≤—Ä–µ–º—è —Ä–∞–∑—Ä–∞–±–æ—Ç–∫–∏ –≤ —Å–ª—É—á–∞–µ –µ–≥–æ –∏—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏—è.
–û—Å–æ–±–µ–Ω–Ω–æ—Å—Ç–∏ –¥–µ–∫–æ—Ä–∏—Ä–æ–≤–∞–Ω–∏—è –º–µ—Ç–æ–¥–æ–≤ –∫–ª–∞—Å—Å–æ–≤
–ï—Å–ª–∏ –≤—ã –∫–æ–≥–¥–∞-—Ç–æ –ø—ã—Ç–∞–ª–∏—Å—å –Ω–∞–ø–∏—Å–∞—Ç—å —É–Ω–∏–≤–µ—Ä—Å–∞–ª—å–Ω—ã–π –¥–µ–∫–æ—Ä–∞—Ç–æ—Ä –¥–ª—è —Ñ—É–Ω–∫—Ü–∏–∏, –º–µ—Ç–æ–¥–∞ —ç–∫–∑–µ–º–ø–ª—è—Ä–∞ –∫–ª–∞—Å—Å–∞, –º–µ—Ç–æ–¥–∞ –∫–ª–∞—Å—Å–∞ –∏ —Å—Ç–∞—Ç–∏—á–µ—Å–∫–æ–≥–æ –º–µ—Ç–æ–¥–∞, –¥—É–º–∞—é, –≤—ã –∑–Ω–∞–µ—Ç–µ, —á—Ç–æ –µ—Å—Ç—å —Ä—è–¥ —Ç—Ä—É–¥–Ω–æ—Å—Ç–µ–π, —Å–≤—è–∑–∞–Ω–Ω—ã—Ö —Å –ø–µ—Ä–µ–¥–∞—á–µ–π –∞—Ä–≥—É–º–µ–Ω—Ç–æ–≤.
–ü—Ä–µ–¥—Å—Ç–∞–≤–∏–º, —á—Ç–æ –º—ã —Ö–æ—Ç–∏–º –≤—ã–≤–æ–¥–∏—Ç—å –≤ –∫–æ–Ω—Å–æ–ª—å –≤—Ç–æ—Ä–æ–π –ø–µ—Ä–µ–¥–∞–≤–∞–µ–º—ã–π –≤ –≤—ã–∑–æ–≤ –ø–æ–∑–∏—Ü–∏–æ–Ω–Ω—ã–π –∞—Ä–≥—É–º–µ–Ω—Ç –≤–Ω—É—Ç—Ä–∏ –¥–µ–∫–æ—Ä–∞—Ç–æ—Ä–∞. –ü–æ–Ω–∏–º–∞—é, –∑–∞–¥–∞—á–∞ –¥–æ—Å—Ç–∞—Ç–æ—á–Ω–æ –∞–±—Å—Ç—Ä–∞–∫—Ç–Ω–∞—è, –Ω–æ –¥—É–º–∞—é, –∫–∞–∂–¥—ã–π —Å—Ç–∞–ª–∫–∏–≤–∞–ª—Å—è —Å —Ç–µ–º, —á—Ç–æ –µ–≥–æ –∏–Ω—Ç–µ—Ä–µ—Å–æ–≤–∞–ª–∏ –ø–∞—Ä–∞–º–µ—Ç—Ä—ã self, cls –∏–ª–∏ –∫–∞–∫–æ–π –ª–∏–±–æ –µ—â–µ –ø–æ–∑–∏—Ü–∏–æ–Ω–Ω—ã–π –∞—Ä–≥—É–º–µ–Ω—Ç –≤–Ω—É—Ç—Ä–∏ –Ω–∞–ø–∏—Å–∞–Ω–Ω–æ–≥–æ –≤–∞–º–∏ –¥–µ–∫–æ—Ä–∞—Ç–æ—Ä–∞.
–†–µ–∞–ª–∏–∑—É–µ–º –æ–ø–∏—Å–∞–Ω–Ω—ã–π –≤—ã—à–µ –¥–µ–∫–æ—Ä–∞—Ç–æ—Ä.
def decorator(function):
    @wraps(function)
    def wrapper(*args, **kwargs):
        print("–í—Ç–æ—Ä–æ–π –ø–æ–∑–∏—Ü–∏–æ–Ω–Ω—ã–π –∞—Ä–≥—É–º–µ–Ω—Ç –≤ –¥–µ–∫–æ—Ä–∞—Ç–æ—Ä–µ", args[1])
        return  function(*args, **kwargs)
    return wrapper
–¢–µ–ø–µ—Ä—å –Ω–∞–ø–∏—à–µ–º –ø—Ä–∏–º–µ—Ä –æ–±—ä–µ–∫—Ç–æ–≤, —Å –∫–æ—Ç–æ—Ä—ã–º–∏ –Ω–∞–º –ø—Ä–µ–¥—Å—Ç–æ–∏—Ç —Ä–∞–±–æ—Ç–∞—Ç—å.
@decorator
def foo(*args, **kwargs):
    print("–ê—Ä–≥—É–º–µ–Ω—Ç—ã —Ñ—É–Ω–∫—Ü–∏–∏", args, kwargs)


class FooClass:
    @decorator
    def method(self, *args, **kwargs):
        print("–ê—Ä–≥—É–º–µ–Ω—Ç—ã —Ñ—É–Ω–∫—Ü–∏–∏", self, args, kwargs)

    @classmethod
    @decorator
    def class_method(cls, *args, **kwargs):
        print("–ê—Ä–≥—É–º–µ–Ω—Ç—ã —Ñ—É–Ω–∫—Ü–∏–∏", cls, args, kwargs)

    @staticmethod
    @decorator
    def static_method(*args, **kwargs):
        print("–ê—Ä–≥—É–º–µ–Ω—Ç—ã —Ñ—É–Ω–∫—Ü–∏–∏", args, kwargs)

foo_instance = FooClass()
–î–æ–ø—É—Å—Ç–∏–º, —á—Ç–æ –º—ã –≤—Å–µ–≥–¥–∞ –∏–º–µ–µ–º –≤–∞–ª–∏–¥–Ω—ã–µ –¥–∞–Ω–Ω—ã–µ –∏ –Ω–µ –±—É–¥–µ–º —É—Ç—Ä—É–∂–¥–∞—Ç—å —Å–µ–±—è —Ä–∞–∑–Ω–æ–≥–æ —Ä–æ–¥–∞ –ø—Ä–æ–≤–µ—Ä–∫–∞–º–∏ –∏ —Ä–∞–±–æ—Ç–æ–π —Å inspect. –ü—Ä–æ—Å—Ç–æ –ø–æ–ø—Ä–æ–±—É–µ–º –≤—ã–ø–æ–ª–Ω–∏—Ç—å –∫–∞–∂–¥—É—é —Ñ—É–Ω–∫—Ü–∏—é.
foo(1, 2)
# –í—Ç–æ—Ä–æ–π –ø–æ–∑–∏—Ü–∏–æ–Ω–Ω—ã–π –∞—Ä–≥—É–º–µ–Ω—Ç –≤ –¥–µ–∫–æ—Ä–∞—Ç–æ—Ä–µ 2
# –ê—Ä–≥—É–º–µ–Ω—Ç—ã —Ñ—É–Ω–∫—Ü–∏–∏ (1, 2) {}
# –¢—É—Ç –º—ã –∏–º–µ–µ–º –æ–∂–∏–¥–∞–µ–º–æ–µ –ø–æ–≤–µ–¥–µ–Ω–∏–µ

FooClass.class_method(1, 2)
# –í—Ç–æ—Ä–æ–π –ø–æ–∑–∏—Ü–∏–æ–Ω–Ω—ã–π –∞—Ä–≥—É–º–µ–Ω—Ç –≤ –¥–µ–∫–æ—Ä–∞—Ç–æ—Ä–µ 1
# –ê—Ä–≥—É–º–µ–Ω—Ç—ã —Ñ—É–Ω–∫—Ü–∏–∏ <class '__main__.FooClass'> (1, 2) {}
# –° –º–µ—Ç–æ–¥–æ–º –∫–ª–∞—Å—Å–∞ —ç—Ç–æ –Ω–µ —Ä–∞–±–æ—Ç–∞–µ—Ç

FooClass.static_method(1, 2)
# –í—Ç–æ—Ä–æ–π –ø–æ–∑–∏—Ü–∏–æ–Ω–Ω—ã–π –∞—Ä–≥—É–º–µ–Ω—Ç –≤ –¥–µ–∫–æ—Ä–∞—Ç–æ—Ä–µ 2
# –ê—Ä–≥—É–º–µ–Ω—Ç—ã —Ñ—É–Ω–∫—Ü–∏–∏ (1, 2) {}
# –°—Ç–∞—Ç–∏—á–µ—Å–∫–∏–π –º–µ—Ç–æ–¥ –ø–æ —Ñ–∞–∫—Ç—É —Ñ—É–Ω–∫—Ü–∏—è, –ø–æ—ç—Ç–æ–º—É –ø—Ä–æ–±–ª–µ–º —Å –Ω–∏–º –Ω–µ—Ç

foo_instance.method(1, 2)
# –í—Ç–æ—Ä–æ–π –ø–æ–∑–∏—Ü–∏–æ–Ω–Ω—ã–π –∞—Ä–≥—É–º–µ–Ω—Ç –≤ –¥–µ–∫–æ—Ä–∞—Ç–æ—Ä–µ 1
# –ê—Ä–≥—É–º–µ–Ω—Ç—ã —Ñ—É–Ω–∫—Ü–∏–∏ <__main__.FooClass object at 0x72efab2423c0> (1, 2) {}
# –° –º–µ—Ç–æ–¥–æ–º —ç–∫–∑–µ–º–ø–ª—è—Ä–∞ —ç—Ç–æ —Ç–∞–∫–∂–µ –Ω–µ —Ä–∞–±–æ—Ç–∞–µ—Ç
–ï—Å—Ç—å –Ω–µ—Å–∫–æ–ª—å–∫–æ –≤–∞—Ä–∏–∞–Ω—Ç–æ–≤ —Ç–æ–≥–æ, –∫–∞–∫ –º—ã –º–æ–∂–µ–º —Ä–µ—à–∏—Ç—å —ç—Ç—É –ø—Ä–æ–±–ª–µ–º—É.
–ú—ã –º–æ–∂–µ–º —è–≤–Ω–æ –æ–±–æ–∑–Ω–∞—á–∏—Ç—å, —á—Ç–æ –¥–µ–∫–æ—Ä–∏—Ä—É–µ–º –º–µ—Ç–æ–¥ –∫–ª–∞—Å—Å–∞ –∏–ª–∏ —ç–∫–∑–µ–º–ø–ª—è—Ä–∞, –¥–æ—Ä–∞–±–æ—Ç–∞–≤ –¥–µ–∫–æ—Ä–∞—Ç–æ—Ä —Ç–∞–∫, —á—Ç–æ–±—ã –æ–Ω –ø—Ä–∏–Ω–∏–º–∞–ª –∞—Ä–≥—É–º–µ–Ω—Ç—ã.
def decorator(
    _function: Callable | None = None,
    /,
    class_method: bool = False
):
    def _decorator(function):
        @wraps(function)
        def wrapper(*args, **kwargs):
            # –ï—Å–ª–∏ —É–∫–∞–∑–∞–Ω–æ, —á—Ç–æ –¥–µ–∫–æ—Ä–∏—Ä—É–µ—Ç—Å—è –º–µ—Ç–æ–¥ –∫–ª–∞—Å—Å–∞,
            # –Ω–∞—Å –∏–Ω—Ç–µ—Ä–µ—Å—É–µ—Ç –∏–Ω–¥–µ–∫—Å 2 –∏–Ω–∞—á–µ 1
            second_arg_index = 2 if class_method else 1
            print(
              "–í—Ç–æ—Ä–æ–π –ø–æ–∑–∏—Ü–∏–æ–Ω–Ω—ã–π –∞—Ä–≥—É–º–µ–Ω—Ç –≤ –¥–µ–∫–æ—Ä–∞—Ç–æ—Ä–µ", 
              args[second_arg_index]
            )
            return  function(*args, **kwargs)
        return wrapper

    # –ï—Å–ª–∏ decorator –≤—ã–∑–≤–∞–Ω –±–µ–∑ –ø–∞—Ä–∞–º–µ—Ç—Ä–æ–≤, —Ç–æ —Å—Ä–∞–∑—É –¥–µ–∫–æ—Ä–∏—Ä—É–µ–º —Ñ—É–Ω–∫—Ü–∏—é
    if _function:
        return _decorator(_function)
    return _decorator
–ü—Ä–æ–≤–µ—Ä—è–µ–º, —á—Ç–æ —Ç–µ–ø–µ—Ä—å –≤—Å–µ —Ä–∞–±–æ—Ç–∞–µ—Ç –∫–æ—Ä—Ä–µ–∫—Ç–Ω–æ.
class FooClass:
    @decorator(class_method=True)
    def method(self, *args, **kwargs):
        print("–ê—Ä–≥—É–º–µ–Ω—Ç—ã —Ñ—É–Ω–∫—Ü–∏–∏", self, args, kwargs)

    @classmethod
