class Decorator:
    _function: Callable

    def __init__(self, function: Callable):
        # –ó–∞–ø–∏—Å—ã–≤–∞–µ–º –¥–µ–∫–æ—Ä–∏—Ä—É–µ–º—É—é —Ñ—É–Ω–∫—Ü–∏—é –≤ –∞—Ç—Ä–∏–±—É—Ç –∫–ª–∞—Å—Å–∞ –¥–µ—Å–∫—Ä–∏–ø—Ç–æ—Ä–∞
        self._function = function

    def __call__(self, *args, __bind_self: bool = False, **kwargs):
        # –ï—Å–ª–∏ –±—ã–ª –ø–µ—Ä–µ–¥–∞–Ω __bind_self —Å–æ –∑–Ω–∞—á–µ–Ω–∏–µ–º True, —Ç–æ –º—ã –∏–º–µ–µ–º
        # –¥–µ–ª–æ —Å –º–µ—Ç–æ–¥–æ–º –∏–Ω—Å—Ç–∞–Ω—Å–∞ –∫–ª–∞—Å—Å–∞
        # –ï—Å–ª–∏ self._is_method(args[0]), —Ç–æ –º—ã –∏–º–µ–µ–º –¥–µ–ª–æ —Å classmethod
        # –ï—Å–ª–∏ —É–∫–∞–∑–∞–Ω–æ, —á—Ç–æ –¥–µ–∫–æ—Ä–∏—Ä—É–µ—Ç—Å—è –º–µ—Ç–æ–¥ –∫–ª–∞—Å—Å–∞, 
        # –Ω–∞—Å –∏–Ω—Ç–µ—Ä–µ—Å—É–µ—Ç –∏–Ω–¥–µ–∫—Å 2 –∏–Ω–∞—á–µ 1
        is_bind = __bind_self or args and self._is_method(args[0])
        second_arg_index = (2 if is_bind else 1)
        print(
          "–í—Ç–æ—Ä–æ–π –ø–æ–∑–∏—Ü–∏–æ–Ω–Ω—ã–π –∞—Ä–≥—É–º–µ–Ω—Ç –≤ –¥–µ–∫–æ—Ä–∞—Ç–æ—Ä–µ",
          args[second_arg_index]
        )
        # –í–æ–∑–≤—Ä–∞—â–∞–µ–º —Ä–µ–∑—É–ª—å—Ç–∞—Ç –≤—ã–ø–æ–ª–Ω–µ–Ω–∏—è —Ñ—É–Ω–∫—Ü–∏–∏
        return self._function(*args, **kwargs)

    def __get__(self, instance, owner):
        # –ü—Ä–∏ –æ–±—Ä–∞—â–µ–Ω–∏–∏ –∫ –¥–µ–∫–æ—Ä–∏—Ä—É–µ–º—ã–º –º–µ—Ç–æ–¥–∞–º –∏–Ω—Å—Ç–∞–Ω—Å–∞ –∫–ª–∞—Å—Å–∞, 
        # –±—É–¥–µ—Ç –≤—ã–∑—ã–≤–∞—Ç—å—Å—è –¥–∞–Ω–Ω–∞—è —Ñ—É–Ω–∫—Ü–∏—è
        # functools.partial –ø—Ä–∏–Ω–∏–º–∞–µ—Ç —Ñ—É–Ω–∫—Ü–∏—é, –∫–æ—Ç–æ—Ä—É—é –Ω—É–∂–Ω–æ 
        # –±—É–¥–µ—Ç –≤—ã–∑–≤–∞—Ç—å –∏ —á–∞—Å—Ç—å –∞—Ä–≥—É–º–µ–Ω—Ç–æ–≤, –∫–æ—Ç–æ—Ä—ã–µ –µ–π –±—É–¥—É—Ç –ø–µ—Ä–µ–¥–∞–Ω—ã
        # –Ω–∞ –¥–∞–Ω–Ω–æ–º —ç—Ç–∞–ø–µ —Å–∞–º–∞ —Ñ—É–Ω–∫—Ü–∏—è –≤—ã–∑–≤–∞–Ω–∞ –Ω–µ –±—É–¥–µ—Ç
        return functools.partial(
            self.__call__,
            instance, 
            # –∞—Ä–≥—É–º–µ–Ω—Ç _Decorator__bind_self –∏–º–µ–µ—Ç —Ç–∞–∫–æ–π –≤–∏–¥
            # –∏–∑-–∑–∞ –æ—Å–æ–±–µ–Ω–Ω–æ—Å—Ç–∏ —Ä–µ–∞–ª–∏–∑–∞—Ü–∏–∏ –ø–µ—Ä–µ–¥–∞—á–∏ –ø—Ä–∏–≤–∞—Ç–Ω—ã—Ö –∞—Ä–≥—É–º–µ–Ω—Ç–æ–≤
            _Decorator__bind_self=instance is not None
        )

    def _is_method(self, arg):
        # –ü—Ä–æ–≤–µ—Ä—è–µ–º –∏–º–µ–µ—Ç –ª–∏ –∞—Ä–≥—É–º–µ–Ω—Ç –∞—Ç—Ä–∏–±—É—Ç —Å –∏–º–µ–Ω–µ–º –¥–µ–∫–æ—Ä–∏—Ä—É–µ–º–æ–π —Ñ—É–Ω–∫—Ü–∏–∏
        attr = getattr(arg, self._function.__name__, None)
        # –ü—Ä–æ–≤–µ—Ä—è–µ–º —è–≤–ª—è—é—Ç—Å—è –ª–∏ —Ñ—É–Ω–∫—Ü–∏–∏ –≤ –æ–±—ä–µ–∫—Ç–µ –∏ –¥–µ–∫–æ—Ä–∏—Ä—É–µ–º–∞—è
        # —Ñ—É–Ω–∫—Ü–∏—è –æ–¥–Ω–∏–º –∏ —Ç–µ–º –∂–µ –æ–±—ä–µ–∫—Ç–æ–º
        return getattr(attr, "_function", None) is self._function
