# –û–∂–∏–¥–∞–µ–º–æ –ø–æ–ª—É—á–∞–µ–º —Ä–µ–∑—É–ª—å—Ç–∞—Ç –≤—ã–ø–æ–ª–Ω–µ–Ω–∏—è —Ñ—É–Ω–∫—Ü–∏–∏ get_current_user_id
–¢–µ–ø–µ—Ä—å –¥–∞–≤–∞–π—Ç–µ —Ä–∞—Å—Å–º–æ—Ç—Ä–∏–º —Ç—Ä–µ—Ç–∏–π –∫–µ–π—Å. –ü–æ–ø—Ä–æ–±—É–µ–º –∏—Å–ø–æ–ª—å–∑–æ–≤–∞—Ç—å –∫—ç—à–∏—Ä–æ–≤–∞–Ω–∏–µ —Ä–µ–∑—É–ª—å—Ç–∞—Ç–æ–≤ –Ω–∞ –æ—Å–Ω–æ–≤–µ –ø–µ—Ä–µ–¥–∞–Ω–Ω—ã—Ö –ø–∞—Ä–∞–º–µ—Ç—Ä–æ–≤.
–î–ª—è –ø—Ä–æ—Å—Ç–æ—Ç—ã –≤ –ø—Ä–∏–º–µ—Ä–µ –≤ –∫–∞—á–µ—Å—Ç–≤–µ —Ö—Ä–∞–Ω–∏–ª–∏—â–∞ –∫—ç—à–∞ —è –±—É–¥—É –∏—Å–ø–æ–ª—å–∑–æ–≤–∞—Ç—å —Å–ª–æ–≤–∞—Ä—å, –∏ –Ω–µ –±—É–¥—É –±—Ä–∞—Ç—å –≤ —Ä–∞—Å—á–µ—Ç —Å—Ä–æ–∫ –µ–≥–æ —Ö—Ä–∞–Ω–µ–Ω–∏—è, –Ω–æ –Ω–∞ –ø—Ä–∞–∫—Ç–∏–∫–µ, –∫–æ–Ω–µ—á–Ω–æ, —Å—Ç–æ–∏—Ç –∏—Å–ø–æ–ª—å–∑–æ–≤–∞—Ç—å redis –∏–ª–∏ –¥—Ä—É–≥–∏–µ –∞–Ω–∞–ª–æ–≥–∏, –∏ –ø–æ–∑–∞–±–æ—Ç–∏—Ç—å—Å—è –æ —Å–≤–æ–µ–≤—Ä–µ–º–µ–Ω–Ω–æ–π –µ–≥–æ –æ—á–∏—Å—Ç–∫–µ.
–ù–∞–ø–∏—à–µ–º –¥–µ–∫–æ—Ä–∞—Ç–æ—Ä, –∫–æ—Ç–æ—Ä—ã–π —É–¥–æ–≤–ª–µ—Ç–≤–æ—Ä—è–µ—Ç –æ–ø–∏—Å–∞–Ω–Ω—ã–º –≤—ã—à–µ —É—Å–ª–æ–≤–∏—è–º.
_cache = {}

def _key_func(*args: Any, **kwargs: Any) -> str:
    # –¢—É—Ç –º–æ–∂–µ—Ç –±—ã—Ç—å –ª—é–±–∞—è —Ñ—É–Ω–∫—Ü–∏—è, –∫–æ—Ç–æ—Ä–∞—è —Å–æ–∑–¥–∞–µ—Ç —Å—Ç—Ä–æ–∫–æ–≤—ã–π –∫–ª—é—á
    # –Ω–∞ –æ—Å–Ω–æ–≤–∞–Ω–∏–∏ –ø–µ—Ä–µ–¥–∞–Ω–Ω—ã—Ö –∞—Ä–≥—É–º–µ–Ω—Ç–æ–≤
    key = args
    if kwargs:
        key += tuple(sorted(kwargs.items()))
    return base64.b64encode(pickle.dumps(key)).decode("utf-8")


def cache_decorator(*params: str) -> Callable:
    # –î–µ–∫–æ—Ä–∞—Ç–æ—Ä, –∫–æ—Ç–æ—Ä—ã–π –±—É–¥–µ—Ç –∑–∞–ø–∏—Å—ã–≤–∞—Ç—å –∫—ç—à –Ω–∞ –æ—Å–Ω–æ–≤–µ –ø–µ—Ä–µ–¥–∞–Ω–Ω—ã—Ö 
    # –∑–Ω–∞—á–µ–Ω–∏–π –ø–∞—Ä–∞–º–µ—Ç—Ä–æ–≤, —É–∫–∞–∑–∞–Ω–Ω—ã—Ö –≤ params
    def decorator(function: Callable) -> Callable:
        # –§—É–Ω–∫—Ü–∏—è –¥–ª—è –ø–æ–ª—É—á–µ–Ω–∏—è –∫–ª—é—á–∞ –∏–∑ –ø–∞—Ä–∞–º–µ—Ç—Ä–æ–≤
        def _get_key(*args, **kwargs):
            # –¢—É—Ç –º—ã –ø–æ–ª—É—á–∞–µ–º —Å–ª–æ–≤–∞—Ä—å —Å–æ –∑–Ω–∞—á–µ–Ω–∏—è–º–∏ –ø–∞—Ä–∞–º–µ—Ç—Ä–æ–≤, 
            # —É–∫–∞–∑–∞–Ω–Ω—ã—Ö –≤ params
            args_values = get_params_with_values(
                inspect.unwrap(function), 
                *args,
                params=params,
                **kwargs
            )
            # –í–æ–∑–≤—Ä–∞—â–∞–µ–º –∫–ª—é—á, —Å–æ–∑–¥–∞–Ω–Ω—ã–π –Ω–∞ –æ—Å–Ω–æ–≤–∞–Ω–∏–∏ –ø–µ—Ä–µ–¥–∞–Ω–Ω—ã—Ö –ø–∞—Ä–∞–º–µ—Ç—Ä–æ–≤
            return str(_key_func(**args_values))

        # –§—É–Ω–∫—Ü–∏—è –¥–ª—è –ø–æ–ª—É—á–µ–Ω–∏—è –∑–Ω–∞—á–µ–Ω–∏–π –∏–∑ –∫—ç—à–∞
        def _get_cached(*args, **kwargs):
            # –ü–æ–ª—É—á–∞–µ–º –∫–ª—é—á
            key = _get_key(*args, **kwargs)
            # –ü—Ä–æ–≤–µ—Ä—è–µ–º –Ω–∞–ª–∏—á–∏–µ –¥–∞–Ω–Ω—ã—Ö –≤ –∫—ç—à–µ
            if key in _cache:
                print("–ü–æ–ª—É—á–µ–Ω–æ –∏–∑ –∫—ç—à–∞")
                # –ü–æ–ª—É—á–∞–µ–º —Ä–µ–∑—É–ª—å—Ç–∞—Ç –∏–∑ –∫—ç—à–∞ –∏ –ø—Ä–µ–æ–±—Ä–∞–∑—É–µ–º –≤ –æ–±—ä–µ–∫—Ç—ã python
                return pickle.loads(_cache.get(key))
            return None

        # –§—É–Ω–∫—Ü–∏—è –¥–ª—è —Å–æ—Ö—Ä–∞–Ω–µ–Ω–∏—è —Ä–µ–∑—É–ª—å—Ç–∞—Ç–∞ –≤ –∫—ç—à
        def _save_result(key, result):
            # –ü—Ä–µ–æ–±—Ä–∞–∑—É–µ–º —Ä–µ–∑—É–ª—å—Ç–∞—Ç –≤ –±–∞–π—Ç—ã
            data = pickle.dumps(result)
            # –ó–∞–ø–∏—Å—ã–≤–∞–µ–º –≤ –∫—ç—à —Ä–µ–∑—É–ª—å—Ç–∞—Ç
            _cache[key] = data

        @wraps(function)
        def wrapper(*args, **kwargs):
            # –ü—Ä–æ–±—É–µ–º –ø–æ–ª—É—á–∏—Ç—å –¥–∞–Ω–Ω—ã–µ –∏–∑ –∫—ç—à–∞
            if cached := _get_cached(*args, **kwargs):
                return cached
            # –ï—Å–ª–∏ –¥–∞–Ω–Ω—ã—Ö –Ω–µ—Ç –≤ –∫—ç—à–µ –≤—ã–∑—ã–≤–∞–µ–º —Ñ—É–Ω–∫—Ü–∏—é
            result = function(*args, **kwargs)
            print("–ü—Ä–æ–∏–∑–æ—à–µ–ª –≤—ã–∑–æ–≤ –¥–µ–∫–æ—Ä–∏—Ä–æ–≤–∞–Ω–Ω–æ–π —Ñ—É–Ω–∫—Ü–∏–∏")
            # –°–æ—Ö—Ä–∞–Ω—è–µ–º —Ä–µ–∑—É–ª—å—Ç–∞—Ç –≤ –∫—ç—à
            _save_result(_get_key(*args, **kwargs), result)
            # –í–æ–∑–≤—Ä–∞—â–∞–µ–º —Ä–µ–∑—É–ª—å—Ç–∞—Ç
            return result

        return wrapper
    return decorator
–ü—Ä–∏–º–µ—Ä –∏—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏—è:
